import React, { Component, useContext } from "react";
/* components */
import MyHeader from "./MyHeader";
import { EditorState, ContentState, convertFromRaw } from "draft-js";
import DraftPasteProcessor from "draft-js/lib/DraftPasteProcessor";
import { Editor } from "react-draft-wysiwyg";
import { stateToHTML } from "draft-js-export-html";
import { stateFromHTML } from "draft-js-import-html";
import { DiaryDispatchContext } from "../App";
import { useNavigate } from "react-router-dom";

function uploadImageCallBack(file) {
  return new Promise((resolve, reject) => {
    const xhr = new XMLHttpRequest();
    xhr.open("POST", "https://api.imgur.com/3/image");
    xhr.setRequestHeader("Authorization", "Client-ID XXXXX");
    const data = new FormData();
    data.append("image", file);
    xhr.send(data);
    xhr.addEventListener("load", () => {
      const response = JSON.parse(xhr.responseText);
      resolve(response);
    });
    xhr.addEventListener("error", () => {
      const error = JSON.parse(xhr.responseText);
      reject(error);
    });
  });
}

class ContentEditor extends Component {
  constructor(props) {
    super(props);
    // 작동은 하지만 처음에 edit을 누르면 기존 텍스트가 나오지 않음
    let editorState;
    if (!this.props.isEdit) {
      // this.state = {
      editorState = EditorState.createEmpty();
      // };
    } else {
      // console.log("contentEditor", this.props.content);
      // this.state = {
      // editorState: EditorState.createWithContent(
      //   ContentState.stateFromHTML(content)
      // editorState: EditorState.createWithContent(
      //   ContentState.createFromText(this.props.content)
      editorState = EditorState.createWithContent(
        stateFromHTML(this.props.content)
      );
      editorState = EditorState.moveFocusToEnd(editorState);
      // };
    }
    this.state = {
      editorState: editorState,
    };
    // 두 번째 방법
    // let editorState;

    // if (props.content) {
    //   // const blocksFromHTML = stateToHTML(props.content);
    //   // const contentState = ContentState.createFromBlockArray(blocksFromHTML);
    //   editorState = EditorState.createWithContent(props.content);
    // } else {
    //   editorState = EditorState.createEmpty();
    // }

    // this.state = { editorState };
  }

  onEditorStateChange: Function = (editorState) => {
    // console.log(editorState)
    // 1
    this.setState({
      editorState,
    });
    // 2
    const editingContent = stateToHTML(editorState.getCurrentContent());
    this.props.setContent(editingContent);
    console.log(editingContent);
  };

  // default
  // exportHTML = () => {
  //   this.setState({
  //     convertedContent: stateToHTML(this.state.editorState.getCurrentContent()),
  //   });
  // };

  // updateHTML = (e) => {
  //   e.preventDefault();
  //   this.setState({ convertedContent: e.target.value });
  // };

  // importHTML = () => {
  //   const { editorState } = this.state;
  //   this.onChange(
  //     EditorState.push(editorState, stateFromHTML(this.state.convertedContent))
  //   );
  // };

  render() {
    const { editorState } = this.state;

    let contentState = stateToHTML(this.state.editorState.getCurrentContent());
    this.props.setContent(contentState);

    // const currentBlockKey = this.state.editorState.getSelection().getStartKey();
    // const currentBlockIndex = this.state.editorState
    //   .getCurrentContent()
    //   .getBlockMap()
    //   .keySeq()
    //   .findIndex((k) => k === currentBlockKey);
    // console.log(currentBlockIndex);

    console.log("ContentEditor", this.props.content);
    return (
      <div className="EditorContainer">
        <div className="editor">
          <Editor
            // editorState={editorState}
            defaultEditorState={this.state.editorState}
            onEditorStateChange={this.onEditorStateChange}
            placeholder="content"
            toolbar={{
              options: [
                "blockType",
                "fontSize",
                "inline",
                "list",
                "textAlign",
                "history",
                "image",
              ],
              list: { inDropdown: true },
              textAlign: { inDropdown: false },
              link: { inDropdown: true },
              history: { inDropdown: false },
              image: {
                uploadCallback: uploadImageCallBack,
                alt: { present: true, mandatory: true },
              },
            }}
            wrapperClassName="wrapper_class"
            editorClassName="editor_class"
            toolbarClassName="toolbar_class"
          />
        </div>
      </div>
    );
  }
}

export default ContentEditor;
